//책에서 로고가 아닌 아래 부분
import { useState } from  "react";
import Main from "../inner/innerMain";
import SignUp from "../inner/innerSignUp";
import FindPW from "../inner/innerFindPW";

const MainInnerStuff = ({login}) =>{
    //가능한 경우 : LoginMain, FindPassword, SignUp
    const [content, setContent] = useState("loginMain");//0:초기화면 1:회원 가입 2:비번 찾기
    const changeContent = (val) => {
        setContent(val);
    };
    const toLoginPage = () => {
        setContent("loginMain");
    };
    const toFindPasswordPage = () => {
        setContent("findPassword");
    };
    const toSignUpPage = () => {
        setContent("signUp");
    };
    return(
        <div>
            {content === "loginMain" ? <Main changeContent={changeContent} login={login}/> : null}
            {content === "findPassword" ? <FindPW changeContent={changeContent} /> : null}
            {content === "signUp" ? <SignUp changeContent={changeContent} /> : null}
        </div>
    )
}

export default MainInnerStuff;
