import { useState } from 'react';
import {
  Route,
  Router,
  Switch,
  Redirect
} from 'react-router-dom';

import Home from "./routes/Home/root/HomeMain";
import Login from "./routes/Login/root/LoginMain";

function App() {
  const [loginState, setLoginState] = useState(false)// 0: 로그인 화면 // 1: 메인화면이다.
  const loginFunc = () => {
    setLoginState(true);
  }
  const logoutFunc = () => {
    setLoginState(false);
  }
  return (
    <div>
      <Router>
        {loginState ? <Redirect to="/" /> : <Redirect to="/main" />}
        <Switch>
          <Route path="/main">
            <Login logout={logoutFunc} />
          </Route>
          <Route path="/">
            <Home login={loginFunc} />
          </Route>
        </Switch>
      </Router>
    </div>
  );
}
//로그인 화면에서는 로그인만 해주면 되고, Home에서는 현재의 login상태를 확인하고, login안되어있으면 login화면으로 보내줘야한다.
export default App;
