import { useState, useEffect } from 'react';
import { useInView } from "react-intersection-observer";
import axios from 'axios';

const pageloadUrl = 'http://52.78.49.137:8080/page/main';

const pageList = [1,2,3,4,5];

const SinglePage = ({ obj, lastRef }) => {
    return(
        <div>
            {
                obj.map((data, index) => (
                    index === obj.length ? <div key={index} ref={lastRef}>{data}</div> : <div key={index}>{data}</div>
                ))
            }
        </div>
    );
}


const LeftPage = () => {
    const [ref, inView] = useInView();//ref를 {ref}로 설정한요소가 화면에 보이는 상황이면 true가 나오고, 아닌 경우 false이다. 


    const pageLoadFunc = () => {
        if(inView){
            axios.get(pageloadUrl + "?startId=-1")
            .then((res) => {
                console.log(res);
            })
            .catch((res) => {
                console.log("error");
                console.log(res);
            })
        }
    };
    useEffect(pageLoadFunc, [inView]);
    return(
        <SinglePage obj={pageList} lastRef={ref}/>
    );
}

export default LeftPage;
