import axios from 'axios';
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from 'react-router-dom';

import Home from "./routes/Home/root/HomeMain";
import Login from "./routes/Login/root/LoginMain";

const kakaotokenUrl = 'http://52.78.49.137:8080/social/login/kakao';
const navertokenUrl = 'http://52.78.49.137:8080/social/login/naver';
const homepageUrl = 'http://localhost:3000/';

function App() {
  //토큰 확인
  let auth = undefined;//아무값이나 넣으면 main페이지 작업가능, undefined로 하면 loginpage작업 가능 추후에 cookie값으로 변경하기

  //로그인 시 페이지 전환하는 함수
  const loginFunc = (res) => {
    const { accessToken } = res.data;
    axios.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`;
  };

  //로그아웃 시 페이지 전환하는 함수
  const logoutFunc = () => {
    axios.post('http://52.78.49.137:8080/user/auth/login',{
      email: 'luckhome1020@gmail.com',
      password: 'qqqq1111!',
    })
    .then((res) => {
      console.log("done")
      console.log("logout");
      axios.get('http://52.78.49.137:8080/user/auth/refresh',{
        headers:{
          cookie: document.cookie,
        }
      })
      .then((res) => console.log(res))
      .catch((res) => {
        console.log("fail");
        console.log(res);
      });
    })
    .catch((res) => console.log("fail"));
  };

  return (
    <Router>

      <Switch>
        <Route path="/main">
          <Home logout={logoutFunc} />
        </Route>
        <Route path="/">
          <Login login={loginFunc} />
        </Route>
      </Switch>
    </Router>
  );
}


export default App;
