import axios from 'axios';
import { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom';

import Home from "./routes/Home/root/HomeMain";
import Login from "./routes/Login/root/LoginMain";

const kakaotokenUrl = 'http://52.78.49.137:8080/social/login/kakao';
const navertokenUrl = 'http://52.78.49.137:8080/social/login/naver';
const homepageUrl = 'http://localhost:3000/';

function App() {
  const [loginState, setLoginState] = useState(true);

  //로그인 시 페이지 전환하는 함수
  const loginFunc = () => {
    setLoginState(true);
  };
  //로그아웃 시 페이지 전환하는 함수
  const logoutFunc = () => {
    setLoginState(false);
  };

  //소셜 로그인 처리 함수
  const socialLogin = (userCode) => {
    axios.post(kakaotokenUrl, {
      code: userCode,
    })
    .then((res) => {
      console.log(res);
      loginFunc();
    })
    .catch((res) => {
      console.log(res);
      loginFunc();//지워야함
    });
  };

  const LoginCheck = () => {
    console.log(loginState)
    if(loginState){
        //window.location.href = "/main"
    }
    else{
      let url = new URL(window.location.href);
      const userCode = url.searchParams.get('code');
      if(userCode !== null){
        socialLogin(userCode);
      }
      else{
        window.location.href="/";
      }
    }
  };
  useEffect(LoginCheck, [loginState]);

  return (
    <Router>
      <Switch>
        <Route path="/main">
          <Home logout={logoutFunc} />
        </Route>
        <Route path="/">
          <Login login={loginFunc} />
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
