import axios from 'axios';
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from 'react-router-dom';
import Home from "./routes/Home/root/HomeMain";
import Login from "./routes/Login/root/LoginMain";

axios.defaults.withCredentials = true;

function App() {
  //로그인함수
  const loginFunc = (res) => {
    //const { accessToken } = res.data;
    //axios.defaults.headers.common['Authorization'] = `${accessToken}`;

    const { refreshToken } = res.headers.authorization;
    console.log(res.res.headers.authorization)
    axios.defaults.headers.common['Authorization'] = `${refreshToken}`;

    axios.get('http://52.78.49.137:8080/user/auth/refresh',)
    .then((res) => console.log("OK"))
    .catch((res) =>{
      console.log("NO");
      console.log(res);
    })
  };

  //로그아웃 함수
  const logoutFunc = () => {

  };

  return (
    <Router>
      <Switch>
        <Route path="/main">
          <Home logout={logoutFunc} />
        </Route>
        <Route path="/">
          <Login login={loginFunc} />
        </Route>
      </Switch>
    </Router>
  );
}


export default App;
