import axios from 'axios';
import { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom';

import Home from "./routes/Home/root/HomeMain";
import Login from "./routes/Login/root/LoginMain";

const kakaotokenUrl = 'http://52.78.49.137:8080/social/login/kakao';
const navertokenUrl = 'http://52.78.49.137:8080/social/login/naver';
const homepageUrl = 'http://localhost:3000/';

let loginState = true;

function App() {

  //로그인 시 페이지 전환하는 함수
  const loginFunc = () => {
    loginState = true;
    window.location.href = '/main'
  };
  //로그아웃 시 페이지 전환하는 함수
  const logoutFunc = () => {
    loginState = false;
    window.location.href='/';
  };

  return (
    <Router>
      <Switch>
        <Route path="/main">
          <Home logout={logoutFunc} />
        </Route>
        <Route path="/">
          <Login login={loginFunc} />
        </Route>
      </Switch>
    </Router>
  );
}


export default App;
