import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import axios from 'axios';
import { useState } from 'react';

const [loginState, setLoginState] = useState(true);

  //로그인 시 페이지 전환하는 함수
  const loginFunc = () => {
    setLoginState(true);
  };
  //로그아웃 시 페이지 전환하는 함수
  const logoutFunc = () => {
    setLoginState(false);
    setLoginState(false);
    setLoginState(false);
    if(!loginState){
      window.location.href='/';
    }
  };

  //소셜 로그인 처리 함수
  const socialLogin = () => {
    if(!loginState){
      let url = new URL(window.location.href);
      const userCode = url.searchParams.get('code');
      if(userCode !== null){
        axios.post(kakaotokenUrl, {
          code: userCode,
        })
        .then((res) => {
          console.log(res);
          loginFunc();
        })
        .catch((res) => {
          console.log(res);
          loginFunc();//지워야함
        });
      }
    }
  };

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App loginFunc={loginFunc} logoutFunc={logoutFunc} socialLogin={socialLogin} />
  </React.StrictMode>
);
