import axios from 'axios';
import { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from 'react-router-dom';
import Home from "./routes/Home/root/HomeMain";
import Login from "./routes/Login/root/LoginMain";

axios.defaults.withCredentials = true;

const loginApiUrl = 'http://52.78.49.137:8080/user/auth/login';

const REST_API_KEY = '75670ae520e9b0c56500f349b16c3c68';
const REDIRECT_URI = 'http://localhost:3000';
const kakaoLoginApiUrl = `https://kauth.kakao.com/oauth/authorize?client_id=${REST_API_KEY}&redirect_uri=${REDIRECT_URI}&response_type=code`;

const naverLoginApiUrl = 'https://nid.naver.com/oauth2.0/authorize';

const kakaotokenUrl = 'http://52.78.49.137:8080/social/login/kakao';
const navertokenUrl = 'http://52.78.49.137:8080/social/login/naver';

function App() {
  const [loginState, SetLoginState] = useState(false)

  //로그인함수
  const loginFunc = (res) => {
    //const { accessToken } = res.data;
    //axios.defaults.headers.common['Authorization'] = `${accessToken}`;

    const { refreshToken } = res.headers.authorization;
    console.log(res.res.headers.authorization)
    axios.defaults.headers.common['Authorization'] = `${refreshToken}`;

    axios.get('http://52.78.49.137:8080/user/auth/refresh',)
    .then((res) => console.log("OK"))
    .catch((res) =>{
      console.log("NO");
      console.log(res);
    })
  };

  //로그아웃 함수
  const logoutFunc = () => {

  };

  //소셜 로그인 처리 함수
  const socialLogin = (userCode) => {
    axios.post(kakaotokenUrl, {
      code: userCode,
    })
    .then((res) => {
      console.log(res);
      loginFunc();
    })
    .catch((res) => {
      console.log(res);
      loginFunc();//지워야함
    });
  };

  const LoginCheck = () => {
    console.log(loginState);
    if(loginState){
        //window.location.href = "/main"
    }
    else{
      let url = new URL(window.location.href);
      const userCode = url.searchParams.get('code');
      console.log(userCode);
      if(userCode !== null){
        socialLogin(userCode);
      }
      else{
        window.location.href="/";
      }
    }
  };
  useEffect(LoginCheck, [loginState]);

  return (
    <Router>
      <Switch>
        <Route path="/main">
          <Home logout={logoutFunc} />
        </Route>
        <Route path="/">
          <Login login={loginFunc} />
        </Route>
      </Switch>
    </Router>
  );
}


export default App;
