import React from 'react';
import { 
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

import Home from "./routes/Home/HomeMain";
import Login from "./routes/Login/LoginMain";
import SocialLogin from "./routes/Login/SocialLogin";

function App() {
  const [isLogined, setIsLogined] = React.useState(false);
  const loginToggle = () => {
    setIsLogined((current) => !current);
  }
  return (
    <Router>
      <Switch>
        <Route path="/social">
          <SocialLogin loginToggle={loginToggle} />
        </Route>
        <Route path="/">
          <Login loginToggle={loginToggle} />
        </Route>
          <Route path="/main">
            <Home loginToggle={loginToggle} />
          </Route>
      </Switch>
    </Router>
  );
}
//로그인 화면에서는 로그인만 해주면 되고, Home에서는 현재의 login상태를 확인하고, login안되어있으면 login화면으로 보내줘야한다.
export default App;
