import { useState } from 'react';
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from 'react-router-dom';

import Home from "./routes/Home/root/HomeMain";
import Login from "./routes/Login/root/LoginMain";

function App() {
  const [loginState, setLoginState] = useState("notSet");

  //로그인 시 페이지 전환하는 함수
  const loginFunc = () => {
    setLoginState("set");
  };
  //로그아웃 시 페이지 전환하는 함수
  const logoutFunc = () => {
    setLoginState("notSet");
  };
  const socialLoginTrial = () => {
    setLoginState("social");
  }

  return (
    <Router>
      {loginState === "Set" ? <Redirect to="/main" /> : null}
      {loginState === "notSet" ? <Redirect to="/" /> : null}
      <Switch>
        <Route path="/main">
          <Home logout={logoutFunc} />
        </Route>
        <Route path="/">
          <Login login={loginFunc} />
        </Route>
      </Switch>
    </Router>
  );
}
//로그인 화면에서는 로그인만 해주면 되고, Home에서는 현재의 login상태를 확인하고, login안되어있으면 login화면으로 보내줘야한다.
export default App;
