import axios from 'axios';
import { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from 'react-router-dom';

import Home from "./routes/Home/root/HomeMain";
import Login from "./routes/Login/root/LoginMain";

const kakaotokenUrl = 'http://52.78.49.137:8080/social/login/kakao';
const navertokenUrl = 'http://52.78.49.137:8080/social/login/naver';
const homepageUrl = 'http://localhost:3000/';

function App() {
  //토큰 확인
  let auth = document.cookie['X-AUTH-TOKEN'];//아무값이나 넣으면 main페이지 작업가능, undefined로 하면 loginpage작업 가능 추후에 cookie값으로 변경하기

  //로그인 시 페이지 전환하는 함수
  const loginFunc = (res) => {
    auth = document.cookie['X-AUTH-TOKEN']
    console.log(auth);
  };
  //로그아웃 시 페이지 전환하는 함수
  const logoutFunc = () => {
    auth = document.cookie['X-AUTH-TOKEN']
  };

  return (
    <Router>

      <Switch>
        <Route path="/main">
          <Home logout={logoutFunc} />
        </Route>
        <Route path="/">
          <Login login={loginFunc} />
        </Route>
      </Switch>
    </Router>
  );
}


export default App;
