import axios from 'axios';
import { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from 'react-router-dom';
import Home from "./routes/Home/root/HomeMain";
import Login from "./routes/Login/root/LoginMain";

axios.defaults.withCredentials = true;

const logoutApiUrl = 'http://52.78.49.137:8080/user/auth/logout';

function App() {

  //로그인함수
  const loginFunc = (res) => {
    localStorage.setItem("rft", res.headers.authorization);//refresh token 저장
    window.location.href="/main";
  };

  //로그아웃 함수
  const logoutFunc = () => {
    localStorage.removeItem("rft");//refresh token 지우기
    axios.get(logoutApiUrl)
    .then((res) => alert("logout success"))
    .catch((res)=>{
      console.log("error")
      console.log(res);
    })
    window.location.href="/";
  };

  return (
    <Router>
      {localStorage.getItem("rft") === null ? <Redirect to='/' /> : <Redirect to="/main" />}
      <Switch>
        <Route path="/main">
          <Home logout={logoutFunc} />
        </Route>
        <Route path="/">
          <Login login={loginFunc} />
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
