import { useState } from 'react';
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from 'react-router-dom';

import Home from "./routes/Home/root/HomeMain";
import Login from "./routes/Login/root/LoginMain";

function App() {
  const [loginState, setLoginState] = useState(true);
  const loginFunc = () => {
    setLoginState(true);
  };
  const logoutFunc = () => {
    setLoginState(false);
  };
  return (
    <Router>
      {loginState ? <Redirect to="/main" /> : <Redirect to="/" />}
      <Switch>
        <Route path="/main">
          <Home logout={logoutFunc} />
        </Route>
        <Route path="/">
          <Login login={loginFunc} />
        </Route>
      </Switch>
    </Router>
  );
}
//로그인 화면에서는 로그인만 해주면 되고, Home에서는 현재의 login상태를 확인하고, login안되어있으면 login화면으로 보내줘야한다.
export default App;
