import React from 'react';
import { Link } from "react-router-dom";
import Style from "./HomeMain.module.css";
import Members from "../../footer/footer";

const Home = ({loginToggle, isLogined}) => {
  const loginCheck = () => {
    if(isLogined){//로그인 된 상황이면 화면을 로딩하기위해 API를 호출

    }
    else{//로그인 되지 않은 상황에서 들어오려고 했으므로 즉시 login화면으로 보낸다.
      window.location.href="/login";
    }
  }
  React.useEffect(loginCheck);
    return(
      <div className={Style.pageCover}>
        <div />
        <div className={Style.mainPage}>
          <h1 className={Style.Check}>Hi</h1>
        </div>
        <div />
        <Members />
      </div>
    );
  }
  
export default Home;
